typedef long long ll;
typedef long double ld;

using perm = vector<int>;

perm one(int n) {
  perm p = perm(n);
  for (int i = 0; i < n; i++) {
    p[i] = i;
  }
  return p;
}

perm operator*(const perm& a, const perm& b) {
  perm res(a.size());
  for (int i = 0; i < (int)a.size(); i++) {
    res[i] = b[a[i]];
  }
  return res;
}

perm operator^(perm a, int k) {
  perm res = one(a.size());
  while (k) {
    if (k & 1) res = res * a;
    a = a * a;
    k /= 2;
  }
  return res;
}

perm operator~(const perm& a) {
  perm res(a.size());
  for (int i = 0; i < (int)a.size(); i++) {
    assert(0 <= a[i] && a[i] < (int)a.size());
    res[a[i]] = i;
  }
  return res;
}

int main() {
#ifdef LOCAL
  freopen("d.in", "r", stdin);
  freopen("d.out", "w", stdout);
#endif

  int n, k;
  while (scanf("%d%d", &n, &k) == 2) {
    perm p(n), q(n);
    for (int i = 0; i < n; i++) {
      scanf("%d", &p[i]);
      --p[i];
    }
    for (int i = 0; i < n; i++) {
      scanf("%d", &q[i]);
      --q[i];
    }

    perm lm, rm;

    int deg = (k - 12) / 6;
    if (deg > 0) {
      perm ip = ~p;
      perm iq = ~q;
      lm = (iq * p * q * ip);
      rm = (p * iq * ip * q);
      lm = lm ^ deg;
      rm = rm ^ deg;
      k -= deg * 6;
    } else {
      lm = rm = one(n);
    }

    for (int i = 2; i < k; i++) {
      perm t = (~p) * q;
      p = q;
      q = t;
    }

    perm ans;
    if (k == 1) {
      ans = p;
    } else {
      ans = q;
    }

    ans = lm * ans * rm;

    for (int i : ans) {
      printf("%d ", i + 1);
    }
    printf("\n");
  }

  return 0;
}

