void merge_sort(int *t1, int *s1, int left, int right){
    if(left==right){
        ;
    }
    else{
        int n2=(right-left+1)/2;
        int n3=right-left+1-n2;
        int i=0,j=0,k=0;

        merge_sort(t1, s1, left, left+n2-1);
        merge_sort(t1, s1, left+n2, right);

        int *t2=(int *)malloc(sizeof(int)*n2);
        int *t3=(int *)malloc(sizeof(int)*n3);
        int *s2=(int *)malloc(sizeof(int)*n2);
        int *s3=(int *)malloc(sizeof(int)*n3);

        for(i=0;i<n2;i++){
          t2[i]=t1[left+i];
          s2[i]=s1[left+i];
        }
        for(i=0;i<n3;i++){
          t3[i]=t1[left+n2+i];
          s3[i]=s1[left+n2+i];
        }

        //n
        for(i=0;i<n2+n3;i++){
            if(k==n3 || (j<n2 && t2[j]<t3[k])){
                t1[left+i]=t2[j];
                s1[left+i]=s2[j];
                j++;
            }
            else{
                t1[left+i]=t3[k];
                s1[left+i]=s3[k];
                k++;
            }
        }
    }
}


// (p^a)(q^b) 
//(0~N-1)
void replace(int N, int *p, int a, int *q, int b, int *pro2){
  int *temp=(int *)malloc(sizeof(int)*N);
  int *p1=(int *)malloc(sizeof(int)*N);
  int *q1=(int *)malloc(sizeof(int)*N);
  int *p2=(int *)malloc(sizeof(int)*N);
  int *q2=(int *)malloc(sizeof(int)*N);
  rep(i,N){
    p1[i]=i;
    q1[i]=i;
    p2[i]=p[i];
    q2[i]=q[i];
  }


  if(a==-1 && b==-1){
    merge_sort(p2, p1, 0, N-1);
    merge_sort(q2, q1, 0, N-1);
    rep(i,N){
      temp[i]=p1[q1[i]];
    }
    rep(i,N){
      pro2[i]=temp[i];
    }
  }
  else if(a==1 && b==-1){
    merge_sort(p1, p2, 0, N-1);
    merge_sort(q2, q1, 0, N-1);
    rep(i,N){
      temp[i]=p2[q1[i]];
    }
    rep(i,N){
      pro2[i]=temp[i];
    }
  }
  else if(a==-1 && b==1){
    merge_sort(p2, p1, 0, N-1);
    merge_sort(q1, q2, 0, N-1);
    rep(i,N){
      temp[i]=p1[q2[i]];
    }
    rep(i,N){
      pro2[i]=temp[i];
    }
  }
  else{
    // printf("p2 = ");
    // rep(i,N){
    //   printf("%d ", p2[i]);
    // }
    // printf("\n");
    // printf("q2 = ");
    // rep(i,N){
    //   printf("%d ", q2[i]);
    // }
    // printf("\n");
    merge_sort(p1, p2, 0, N-1);
    merge_sort(q1, q2, 0, N-1);

    rep(i,N){
      temp[i]=p2[q2[i]];
    }
    rep(i,N){
      pro2[i]=temp[i];
    }
  }
}

//AB
void make(int N, int *p2, int *q2, int *A2, int *B2, int eqo, int rest, int *pro2){
  if(eqo==0){
    rep(i,N){
      A2[i]=i;
    }
  }
  else if(eqo==1){
    rep(i,N){
      A2[i]=pro2[i];
    }
  }
  else{
    rep(i,N){
      A2[i]=pro2[i];
    }
    rep(i,eqo-1){
      replace(N, A2, 1, pro2, 1, A2);
    }
  }


  if(rest==1){
    rep(i,N){
      B2[i]=p2[i];
    }
  }
  else if(rest==2){
    rep(i,N){
      B2[i]=q2[i];
    }
  }
  else if(rest==3){
    replace(N, q2, 1, p2, -1, B2);
  }
  else if(rest==4){
    replace(N, A2, 1, q2, 1, A2);
    int *p1=(int *)malloc(sizeof(int)*N);
    rep(i,N){
      p1[i]=i;
    }
    replace(N, p1, 1, p2, -1, B2);
  }
  else if(rest==5){
    replace(N, A2, 1, q2, 1, A2);
    // printf("A2 = ");
    // rep(i,N){
    //   printf("%d ", A2[i]);
    // }
    // printf("\n");
    replace(N, A2, 1, p2, -1, A2);
    int *q1=(int *)malloc(sizeof(int)*N);
    rep(i,N){
      q1[i]=i;
    }
    replace(N, q1, 1, q2, -1, B2);
  }
  else{
    replace(N, A2, 1, q2, 1, A2);
    replace(N, A2, 1, p2, -1, A2);
    replace(N, q2, -1, p2, 1, B2);
  }
}



