fun main(args: Array<String>) {
  val (N, K) = readInts()
  val A = readInts()

  fun solve(n: Int): Boolean {
    var current = 0
    for (i in 0 until N) {
      val d = A[i] % n
      val a = (current + d + n) % n
      val b = (current - d + n) % n
      val da = Math.abs(a - n)
      val db = Math.abs(b - n)
      if (da < db) {
        current = a
      } else {
        current = b
      }
    }
    return current == 0
  }

  val pf = primeFactorization(A.sum()).toList()

  var result = 0
  var prods = Array(pf.size) { 0 }
  fun f(i: Int) {
    var p = 1
    for (n in 0..pf[i].second) {
      prods[i] = p
      p *= pf[i].first
      if (i + 1 < pf.size) {
        f(i + 1)
      } else {
        var num = 1
        prods.forEach { num *= it }
        if (solve(num)) result = Math.max(result, num)
      }
    }
  }
  f(0)

  println(result)
}

fun primeFactorization(n: Int): Map<Int, Int> {
  val isPrime = Array(n + 1) { true }
  isPrime[0] = false
  isPrime[1] = false
  var i = 2
  while (i <= n) {
    if (isPrime[i]) {
      var j = i * 2
      while (j <= n) {
        isPrime[j] = false
        j += i
      }
    }
    i++
  }

  var num = n
  val factors = mutableMapOf<Int, Int>()
  for (i in 2..n) {
    if (!isPrime[i]) continue
    var cnt = 0
    while (num % i == 0) {
      cnt++
      num /= i
    }
    if (cnt > 0) factors[i] = cnt
  }
  return factors
}

