public class Main {
	static InputStream is;
	static PrintWriter out;
	static String INPUT = "";

	static void solve()
	{
		int n = ni(), K = ni();
		int[] P = na(n);
		for(int i = 0;i < n;i++)P[i]--;
		int[] Q = na(n);
		for(int i = 0;i < n;i++)Q[i]--;

		// -p, q, p, -q
		// q -p -q p

		/*
[5, [-q]]
[6, [p, -q]]
[7, [q, p, -q]]
[8, [q]]
[9, [q, -p]]
[10, [q, -p, -q]]
		 * 
		 */
		if(K == 1){
			for(int i = 0;i < n;i++){
				out.print(P[i]+1 + " ");
			}
			out.println();
			return;
		}

		int[] A = mul(inv(P), Q, P, inv(Q)); 
		int[] B = mul(Q, inv(P), inv(Q), P);

		int[] f = new int[n];
		for(int i = 0;i < n;i++)f[i] = i;
		f = pow(A, (K-2)/6, f);
		if(K % 6 == 2){
			f = mul(Q, f);
		}else if(K % 6 == 3){
			f = mul(Q, inv(P), f);
		}else if(K % 6 == 4){
			f = mul(Q, inv(P), inv(Q), f);
		}else if(K % 6 == 5){
			f = mul(inv(Q), f);
		}else if(K % 6 == 0){
			f = mul(P, inv(Q), f);
		}else if(K % 6 == 1){
			f = mul(Q, P, inv(Q), f);
		}
		f = pow(B, (K+1)/6, f);

		for(int i = 0;i < n;i++){
			out.print(f[i]+1 + " ");
		}
		out.println();
	}

	static int[] pow(int[] f, int e, int[] a)
	{
		while(e > 0){
			if((e&1) == 1){
				a = mul(f, a);
			}
			f = mul(f, f);
			e>>>=1;
		}
		return a;
	}

	static int[] mul(int[]... x)
	{
		int[] f = mul(x[x.length-2], x[x.length-1]);
		for(int i = x.length-3;i >= 0;i--){
			f = mul(x[i], f);
		}
		return f;
	}

	static int[] mul(int[] f, int[] g)
	{
		int[] ret = new int[f.length];
		for(int i = 0;i < g.length;i++){
			ret[i] = f[g[i]];
		}
		return ret;
	}

	static int[] inv(int[] f)
	{
		int[] ret = new int[f.length];
		for(int i = 0;i < f.length;i++)ret[f[i]] = i;
		return ret;
	}

	static List<String> clean(List<String> x)
	{
		outer:
		while(true){
			for(int i = 0;i < x.size()-1;i++){
				String l = x.get(i);
				String r = x.get(i+1);
				if(l.length() == 1 && r.length() == 2 && l.charAt(0) == r.charAt(1)){
					x.remove(i);
					x.remove(i);
					continue outer;
				}
				if(r.length() == 1 && l.length() == 2 && r.charAt(0) == l.charAt(1)){
					x.remove(i);
					x.remove(i);
					continue outer;
				}
			}
			return x;
		}
	}

	static List<String> rev(List<String> x)
	{
		List<String> y = new ArrayList<>();
		for(int i = x.size()-1;i >= 0;i--){
			String t = x.get(i);
			if(t.length() == 2){
				y.add(t.substring(1));
			}else{
				y.add("-" + t);
			}
		}
		return y;
	}

