public class Main {

	int[] solve() {
		int n = nextInt();
		int b = nextInt() - 1;
		int[] p = new int[n];
		int[] q = new int[n];
		for (int i = 0; i < n; i++) {
			p[i] = nextInt() - 1;
		}
		for (int i = 0; i < n; i++) {
			q[i] = nextInt() - 1;
		}

		if (b == 0) {
			return p;
		}

		int k = (b - 1) / 6;

		int head, tail;
		int[] mid;

		if (b == 6 * k + 1) {
			head = tail = k;
			mid = q;
		} else if (b == 6 * k + 2) {
			head = tail = k;
			mid = mult(inv(p), q);
		} else if (b == 6 * k + 3) {
			head = tail = k;
			mid = mult(inv(q), mult(inv(p), q));
		} else if (b == 6 * k + 4) {
			head = k;
			tail = k + 1;
			mid = inv(q);
		} else if (b == 6 * k + 5) {
			head = k;
			tail = k + 1;
			mid = mult(inv(q), p);
		} else if (b == 6 * k + 6) {
			head = k;
			tail = k + 1;
			mid = mult(mult(inv(q), p), q);
		} else {
			throw new AssertionError();
		}

		// QpqP
		int[] headBlock = mult(mult(mult(inv(q), p), q), inv(p));

		int[] tailBlock = inv(headBlock);

		int[] ret = mult(mult(pow(headBlock, head), mid), pow(tailBlock, tail));

		return ret;
	}

	char flip(char c) {
		if (Character.isLowerCase(c)) {
			return Character.toUpperCase(c);
		} else {
			return Character.toLowerCase(c);
		}
	}

	String inverse(String a) {
		StringBuilder sb = new StringBuilder();
		for (int i = a.length() - 1; i >= 0; i--) {
			sb.append(flip(a.charAt(i)));
		}
		return sb.toString();
	}

	String simplify(String a) {
		StringBuilder sb = new StringBuilder();
		for (int i = 0; i < a.length(); i++) {
			// sb.append(flip(a.charAt(i)));
			if (sb.length() > 0
					&& flip(a.charAt(i)) == sb.charAt(sb.length() - 1)) {
				sb.deleteCharAt(sb.length() - 1);
			} else {
				sb.append(a.charAt(i));
			}
		}
		return sb.toString();
	}

	String f(String a, String b) {
		return simplify(inverse(a) + b);
	}

	int[] inv(int[] p) {
		int[] q = new int[p.length];
		for (int i = 0; i < p.length; i++) {
			q[p[i]] = i;
		}
		return q;
	}

	int[] mult(int[] a, int[] b) {
		int[] c = new int[a.length];
		for (int i = 0; i < a.length; i++) {
			c[i] = b[a[i]];
		}
		return c;
	}

	int[] pow(int[] a, int b) {
		int[] ret = IntStream.range(0, a.length).toArray();
		for (; b > 0; b >>= 1) {
			if ((b & 1) == 1) {
				ret = mult(ret, a);
			}
			a = mult(a, a);
		}
		return ret;
	}

