            ret[i] = new int[2];
            for (int j = 0; j < 2; j++)
            {
                ret[i][j] = A[i][0] * B[0][j] + A[i][1] * B[1][j];
            }
        }
        return ret;
    }
    static int[][] pow(int[][] A, long k) {
        if (k == 1) {
            return A;
        }
        var t = pow(A, k / 2);
        t = mul2(t, t);
        if (k % 2 == 1) t = mul2(t, A);
        return t;
    }
