            var ret = new int[p.Length];
            for (int i = 0; i < ret.Length; i++)
            {
                ret[i] = i;
            }
            return ret;
        }
        var t = pow(p, k / 2);
        t = mul(t, t);
        if (k % 2 == 1) t = mul(t, p);
        return t;
    }

