using System;
using System.Text;
using System.Collections.Generic;
using System.Linq;
using System.Numerics;
class AtCoder
{
    static int[] f(int[] p, int[] q)
    {
        int[] ret = new int[p.Length];
        foreach(int i in Enumerable.Range(0, ret.Length))
        {
            ret[p[i]] = q[i];
        }
        return ret;
    }
    const int MOD = 1000000007;
    public void Slove()
    {
        var inputs = Console.ReadLine().Split();
        var n = int.Parse(inputs[0]);
        var k = int.Parse(inputs[1]) - 2;
        var p = Console.ReadLine().Split().Select(x => int.Parse(x) - 1).ToArray();
        var q = Console.ReadLine().Split().Select(x => int.Parse(x) - 1).ToArray();

        int count = 0;
        var a1 = new int[n];
        var a2 = new int[n];
        p.CopyTo(a1, 0);
        q.CopyTo(a2, 0);
        foreach(int i in Enumerable.Range(1, k))
        {
            //System.Console.WriteLine(string.Join(" ",a2.Select(x => x + 1)));
            int[] temp = new int[n];
            a2.CopyTo(temp, 0);    
            a2 = f(a1, a2);
            a1 = temp;
            if(a1.SequenceEqual(p) && a2.SequenceEqual(q))
            {
                count = i;
                //System.Console.WriteLine($"end {count}");
                break;
            }
        }
        if(count == 0)
            System.Console.WriteLine(string.Join(" ",a2.Select(x => x + 1)));
        else
        { 
            foreach(int i in Enumerable.Range(0, k % count))
            {
                int[] temp = new int[n];
                a2.CopyTo(temp, 0);
                a2 = f(a1, a2);
                a1 = temp;
            }
            System.Console.WriteLine(string.Join(" ",a2.Select(x => x + 1)));            
        }
    }
    static void Main()
    {
        var ac = new AtCoder();
        ac.Slove();
    }
}

