int N, K;
int[] P, Q;

int[] inv(in int[] a) {
  int[] b = new int[N];
  foreach (i; 0 .. N) {
    b[a[i]] = i;
  }
  return b;
}

int[] mul(in int[] a, in int[] b) {
  int[] c = new int[N];
  foreach (i; 0 .. N) {
    c[i] = a[b[i]];
  }
  return c;
}

int[] power(in int[] a, int e) {
  int[] b = a.dup;
  int[] c = new int[N];
  foreach (i; 0 .. N) {
    c[i] = i;
  }
  for (; e; e >>= 1) {
    if (e & 1) {
      c = c.mul(b);
    }
    b = b.mul(b);
  }
  return c;
}

void main() {
  try {
    for (; ; ) {
      N = readInt();
      K = readInt();
      P = new int[N];
      foreach (i; 0 .. N) {
        P[i] = readInt() - 1;
      }
      Q = new int[N];
      foreach (i; 0 .. N) {
        Q[i] = readInt() - 1;
      }

      int[] ans;
      if (K == 1) {
        ans = P;
      } else if (K == 2) {
        ans = Q;
      } else {
        const x = (K + 1) * 2 / 3;
        const y = (K - 3) * 2 / 3;
        debug {
          writefln("x = %s, y = %s", x, y);
        }
        int[] invP = P.inv;
        int[] invQ = Q.inv;
        int[] R = Q.mul(invP).mul(invQ).mul(P);
        int[] a = R.power(x / 4);
        foreach (i; 0 .. x % 4) {
          a = a.mul([Q, invP, invQ, P][i]);
        }
        int[] b = R.power(y / 4);
        foreach (i; 0 .. y % 4) {
          b = b.mul([Q, invP, invQ, P][i]);
        }
        ans = a.mul(b.inv);
      }
      foreach (i; 0 .. N) {
        if (i > 0) {
          write(" ");
        }
        write(ans[i] + 1);
      }
      writeln();
    }
  } catch (EOFException e) {
  }
}

