proc gousei_chikan(g: seq[int], f: seq[int]):seq[int] =
  var h = newSeq[int](N)
  for i in 0..<N:
    h[i] = g[f[i]]
  return h

proc gyaku_chikan(f: seq[int]):seq[int] =
  var g = newSeq[int](N)
  for i in 0..<N:
    g[f[i]] = i
  return g

proc id_chikan():seq[int] =
  var f = newSeq[int](N)
  for i in 0..<N:
    f[i] = i
  return f

proc beki_chikan(f:seq[int], n:int):seq[int] =
  if n == 0:
    return id_chikan()

  var kl = int(log2(float(n)))+1
  var g_k = newSeqWith(kl, newSeq[int](N)) # g_k[k] = f^(2^k)
  for k in 0..<kl:
    if k==0:
      g_k[k] = f
    else:
      g_k[k] = gousei_chikan(g_k[k-1], g_k[k-1])

  var re = id_chikan()
  var funi = 0
  for k in 0..<kl:
    if ((n shr k) and 1) == 1:
      re = gousei_chikan(re,g_k[k])
      funi += 1 shl k
  #echo "funi: ", funi
  return re


