fn mulv(p: &Vec<usize>, q: &Vec<usize>) -> Vec<usize> {
    let mut v = vec![0; p.len()];
    for i in 0..p.len() {
        v[i] = p[q[i]];
    }
    v
}

fn inv(v: &Vec<usize>) -> Vec<usize> {
    let mut w = vec![0; v.len()];
    for i in 0..v.len() {
        w[v[i]] = i;
    }
    w
}

fn powv(v: &Vec<usize>, n: usize) -> Vec<usize> {
    if n == 0 {
        return id(v.len());
    } else if n == 1 {
        return v.clone();
    }

    let m = n / 2;
    let mut w = powv(&mulv(v, v), m);
    if n % 2 == 1 {
        w = mulv(&w, v);
    }
    w
}

