fn next(a: &Vec<usize>, b: &Vec<usize>) -> Vec<usize> {
    let mut c = a.clone();
    c.reverse();
    let al = a.len();
    for i in 0..al {
        c[i] ^= 1;
    }

    let bn = b.len();
    for i in 0..bn {
        let n = c.len();
        if c[n-1] ^ 1 == b[i] {
            c.pop();
        } else {
            c.push(b[i]);
        }
    }
    c
}

fn combine(a: &Vec<usize>, b: &Vec<usize>) -> Vec<usize> {
    let n = a.len();
    let mut c = vec![0; n];
    for i in 0..n {
        c[i] = b[a[i]];
    }
    c
}

fn pow(a: &Vec<usize>, mut p: i64) -> Vec<usize> {
    let n = a.len();
    let mut aa = a.clone();
    let mut ret = vec![0; n];
    for i in 0..n {
        ret[i] = i;
    }
    while p >= 1 {
        if p & 1 == 1 {
            ret = combine(&ret, &aa);
        }
        aa = combine(&aa, &aa);
        p >>= 1;
    }
    ret
}

fn inv(a: &Vec<usize>) -> Vec<usize> {
    let n = a.len();
    let mut ret = vec![0; n];
    for i in 0..n {
        ret[a[i]] = i;
    }
    ret
}

fn wl(a: &Vec<usize>, elements: &Vec<Vec<usize>>) -> Vec<usize> {
    let n = elements[0].len();
    let mut ret = vec![0; n];
    for i in 0..n {
        ret[i] = i;
    }
    for ai in a {
        ret = combine(&ret, &elements[*ai]);
    }
    ret
}

