using namespace std;

bool binSum(long long N, long long M){
  if(N == 0) return true;
  if(N == M) return false;

  long long n = N;
  while(n & (n - 1)) n &= n - 1;

  bool res = binSum(N - n, min(M, N - n));
  if(M >= n) res ^= binSum(N - n, M - n);

  return res;
}

long long xs[100002], ys[100002];

int main(){
  int N; scanf("%d", &N);
  for(int i = 1; i <= N; i++) scanf("%lld%lld", &xs[i], &ys[i]);

  long long rs = -200000000000000000, re = 200000000000000000;

  while(rs < re){
    long long gap = re - rs;
    long long m = rs + gap / 2;

    bool odd = false;

    for(int i = 1; i <= N; i++){
      if(ys[i] == 0){
        if(xs[i] <= m) odd ^= 1;
        continue;
      }

      long long s = xs[i], e = xs[i] + ys[i];
      if(s > e) swap(s, e);

      if(e <= m || s > m) continue;

      odd ^= binSum(e - s, m - s);
    }

    if(odd) re = m; else rs = m + 1;
  }

  printf("%lld\n", rs);
  return 0;
}

