int main() {
  cin.tie(nullptr); ios::sync_with_stdio(false);
  int n; cin >> n;
  auto comp = [](const auto& p, const auto& q) { return make_pair(p.second, p.first) < make_pair(q.second, q.first); };
  set< pair<lint, lint>, decltype(comp) > se(comp);
  for (int i = 0; i < n; ++i) {
    lint x, y; cin >> x >> y;
    se.emplace(x, y);
  }
  auto g = [&](lint x, lint y) {
    if (se.count({x, y})) se.erase({x, y});
    else se.emplace(x, y);
  };
  auto f = [&](lint x, lint y, int k) {
    g(x, y);
    g(x, y + (1LL << k));
    g(x + (1LL << k), y);
  };
  for (int k = 0; k < 60; ++k) {
    for (auto itr = begin(se); itr != end(se); ) {
      if (itr->second & 1LL << k) {
        lint x, y;
        tie(x, y) = *itr;
        if (y > 0) f(x, y - (1LL << k), k);
        else if (se.count({x + (1LL << k), y})) f(x, y, k);
        else if (se.count({x - (1LL << k), y})) f(x - (1LL << k), y, k);
        itr = se.lower_bound({x, y});
      } else {
        ++itr;
      }
    }
  }
  cout << begin(se)->first << '\n';
}

