const int MAXN=100000;
struct P { ll x,y; P() {} P(ll x,ll y):x(x),y(y) {} };

int n;
P p[MAXN];

ll qy;

int cntbits(ll dy) { int bits=0; while(dy>0) bits+=dy&1,dy>>=1; return bits; }

ll calcdesc(ll dy) { return 1LL<<cntbits(dy);  }

ll calccnt(P a,ll mx) {
	ll ret=0;
	int bits=cntbits(a.y-qy);
	while(true) {
		if(a.y==qy) { if(a.x<=mx) ++ret; break; }
		int step=0; while(a.y-(2LL<<step)>=qy) ++step;
		//printf("(%lld,%lld) -> step=%d\n",a.x,a.y,step);
		P b(a.x,a.y-(1LL<<step)),c(a.x+(1LL<<step),a.y-(1LL<<step)); --bits;
		//printf("(%lld,%lld) / (%lld,%lld)\n",b.x,b.y,c.x,c.y);
		if(b.x+b.y-qy<=mx) { ret+=1LL<<bits; a=c; } else { a=b; }
	}
	return ret;
}


ll calcsum(ll mx,ll mod) {
	ll ret=0; REP(i,n) ret=(ret+calccnt(p[i],mx))%mod; return ret;
}

ll solve() {
	ll ly=LLONG_MAX; REP(i,n) ly=min(ly,p[i].y);
	ll lx=LLONG_MAX,hx=LLONG_MIN; REP(i,n) lx=min(lx,p[i].x),hx=max(hx,p[i].x+p[i].y-ly);
	ll dx=hx-lx+1; while((dx&-dx)!=dx) dx+=dx&-dx; qy=-dx; assert(qy<=ly);
	//printf("ly=%lld lx=%lld hx=%lld dx=%lld qy=%lld\n",ly,lx,hx,dx,qy);

	/*REP(rep,16) {
		qy=ly-rep; lx=LLONG_MAX,hx=LLONG_MIN; REP(i,n) lx=min(lx,p[i].x),hx=max(hx,p[i].x+p[i].y-qy);
		printf("%3lld: ",qy); for(ll x=lx;x<=hx;++x) printf(" %lld",calcsum(x,1LL<<20)); puts("");
	}*/

	assert(calcsum(lx+dx-1,2)%2==1);
	ll lo=lx-1,hi=lx+dx-1; while(lo+1<hi) { ll mi=lo+(hi-lo)/2; if(calcsum(mi,2)%2==1) hi=mi; else lo=mi; }
	return hi;
}


