class points {
    public:
      map<ll,set<ll>> x, y;

      points() {}

      void add(ll xx, ll yy) {
          x[xx].insert(yy);
          y[yy].insert(xx);
      }

      void remove(ll xx, ll yy) {
          x[xx].erase(yy);
          y[yy].erase(xx);
      }

      void toggle(ll xx, ll yy) {
          if (set_map_includes(x[xx], yy)) {
              remove(xx, yy);
          } else {
              add(xx, yy);
          }
      }
};

