#include <bits/stdc++.h>
using namespace std;

typedef long long ll;

const ll inf = 1e17 + 2;
const int N = 1e5 + 10;

int pos;

vector<int> operator ^ (vector<int> a, vector<int> b) {
  vector<int> c(3);
  for(int i = 0; i < 3; i++) {
    c[i] = a[i] ^ b[i];
  }
  return c;
}

ll f[62][2][3];

vector<int> calc(ll n, ll r, ll x) {
  vector<int> cur(3);
  if(r < 0) {
    return cur;
  }
  memset(f, 0, sizeof f);
  f[60][1][0] = 1;
  for(int i = 59; ~i; i--) {
    for(int j = 0; j < 3; j++) {
      if(f[i + 1][0][j]) {
	f[i][0][(j << 1) % 3] ^= 1;
	if(n >> i & 1) {
	  f[i][0][((j << 1) | 1) % 3] ^= 1;
	}
      }
      if(f[i + 1][1][j]) {
	if(r >> i & 1) {
	  f[i][0][(j << 1) % 3] ^= 1;
	  if(n >> i & 1) {
	    f[i][1][(j << 1 | 1) % 3] ^= 1;
	  }
	} else {
	  f[i][1][(j << 1) % 3] ^= 1;
	}
      }
    }
  }
  x = (x % 3 + 3) % 3;
  for(int i = 0; i < 3; i++) {
    cur[(x + i) % 3] = f[0][0][i] ^ f[0][1][i];
  }
  return cur;
}

int n;
ll x[N], y[N];

bool check(ll lim) {
  vector<int> ans(3);
  for(int i = 1; i <= n; i++) {
    ans = ans ^ calc(y[i] + inf, lim - x[i], x[i]);
  }
  return ans[pos];
}

bool lightnow(ll x, ll y) {
  if(y < 0 || y > x) {
    return 0;
  }
  return (x & y) == y;
}

ll limit(ll cur, ll op) {
  for(int i = 60; ~i; i--) {
    bool light = false;
    ll tmp = cur + (op << i);
    for(int j = 1; j <= n; j++) {
      light ^= lightnow(y[j] + inf, tmp - x[j]);
    }
    if(light) {
      cur = tmp;
    }
  }
  return cur;
}

int main(void) {
  scanf("%d", &n);
  vector<int> cur(3);
  for(int i = 1; i <= n; i++) {
    scanf("%lld%lld", &x[i], &y[i]);
    cur = cur ^ calc(y[i] + inf, y[i] + inf, x[i]);
  }
  for(; !cur[pos]; pos++);
  ll l = -inf * 10, r = inf * 10;
  while(l < r) {
    ll mid = l + r >> 1;
    if(check(mid)) {
      r = mid;
    } else {
      l = mid + 1;
    }
  }
  long long ansl = limit(l, -1), ansr = limit(l, 1);
  cout << ansl << " " << ansr - ansl - inf << endl;
  return 0;
}

