#define F first
#define S second
int n;
vector<pair<long long, long long> > p;
int main()
{
    ios::sync_with_stdio(false);
    cin.tie(0);
    cin>>n;
    p.resize(n);
    long long minf;
    long long maxf;
    for(int i=0; i<n; i++)
    {
        cin>>p[i].F>>p[i].S;
        if(i==0)
        {
            minf=p[i].F;
            maxf=p[i].F;
        }
        else
        {
            minf=min(minf, p[i].F);
            maxf=max(maxf, p[i].F);
        }
    }
    int h=0;
    while((1ll<<(h))<maxf-minf) h++;
    maxf=minf+(1ll<<(h))-1;
    long long start=minf;
    while(h>0)
    {
        long long mmf=maxf-(1ll<<(h-1));
        long long ox=mmf;
        long long oy=minf-mmf;
        int parity=0;
        for(int i=0; i<n; i++)
        {
            if(p[i].F<=ox && p[i].F+p[i].S>=oy)
            {
                long long z1=p[i].S-oy;
                long long z2=ox-p[i].F;
                while(z1>0 && z2>0)
                {
                    if(z2%2==1 && z1%2==0)
                    {
                        parity++;
                        break;
                    }
                    z1/=2;
                    z2/=2;
                }
            }
        }
        if(parity%2==1)
        {
            maxf-=(1ll<<(h-1));
        }
        else
        {
            minf+=(1ll<<(h-1));
        }
        h--;
    }
    cout<<minf<<endl;

    return 0;
}

