bool count(ll y, ll m){
	if(y==0) return 1;
	else if(y>0){
		bool dp[2][59]={};
		for(int i=0; i<59; i++){
			if((1ll<<i)<=m && ((1ll<<i)|y)==y){
				if(m<(1ll<<(i+1))) dp[0][i]=1;
				else dp[1][i]=1;
			}
