bool checkPt(ll Y) {
	bool res = 0;
	for (int i = 0; i < N; i++) {
		res ^= (((Y - P[i].second) | P[i].first) == P[i].first);
	}
	return res;
}

pair<ll, ll> ptToBounds(ll Y) {
	assert(checkPt(Y));
	ll lo = Y, hi = Y;
	for (int l = 59; l >= 0; l--) {
		if (checkPt(hi + (1ll << l))) {
			hi += (1ll << l);
		}
		if (checkPt(lo - (1ll << l))) {
			lo -= (1ll << l);
		}
	}
	return {hi - lo, lo};
}

