struct Point {
  long long x, y;
  bool operator< (const Point &r) const {
    return y == r.y ? x < r.x : y < r.y;
  }
};

bool binSum(long long N, long long M){
  if(N == 0) return true;
  if(N == M) return false;

  long long n = N;
  while(n & (n - 1)) n &= n - 1;

  bool res = binSum(N - n, min(M, N - n));
  if(M >= n) res ^= binSum(N - n, M - n);

  return res;
}

set<Point> st;

int main(){
  int N; scanf("%d", &N);

  for(int i = 1; i <= N; i++){
    long long x, y; scanf("%lld%lld", &x, &y);
    st.insert({ x, y });
  }

  for(;;){
    auto a = st.begin(); if((*a).y >= 0) break;
    long long x = (*a).x, y = (*a).y; st.erase(a);

    for(long long g = 1; ; g *= 2){
      Point p = { x + g, y };
      auto b = st.find(p); if(b == st.end()) continue;
      st.erase(b);

      Point t = { x, x + g + y };
      auto c = st.find(t);
      if(c == st.end()) st.insert(t);
      else st.erase(c);

      break;

    }
  }

  long long rs = -200000000000000000, re = 200000000000000000;

  while(rs < re){
    long long gap = re - rs;
    long long m = rs + gap / 2;

    bool odd = false;

    for(const Point &t : st){
      if(t.y == 0){
        if(t.x <= m) odd ^= 1;
        continue;
      }

      long long s = t.x, e = t.x + t.y;
      if(s > e) swap(s, e);

      if(e <= m || s > m) continue;

      odd ^= binSum(e - s, m - s);
    }

    if(odd) re = m; else rs = m + 1;
  }

  printf("%lld\n", rs);
  return 0;
}

