unordered_map<ll, ll> dh;
unordered_map<ll, int> z;
vector<pair<ll,ll>> uh;

void put_lt(ll x, ll y){
    if(y == 0){
        z[x] += 1;
        z[x+1] -= 1;
        return;
    }
    if(y > 0){
        uh.push_back({x, y});
    }

    if(dh[y] == 0){
        if(x == 0) dh[y] = INT64_MAX;
        else dh[y] = x;
    }else{
        ll x2 = dh[y];
        if(x2 == INT64_MAX) x2 = 0;
        dh[y] = 0;
        if(x == x2) return;
        if(x > x2){
            ll tmp = x;
            x = x2;
            x2 = x;
        }
        ll dist = x2-x;
        ll px = x;
        for(int b=63;b>=0;b--){
            if(((1LL<<b) & dist) != 0){
                put_lt(px, y+(1LL<<b));
                px += (1LL<<b);
            }
        }
    }
}

