        z[p.first]++;
        z[p.first+p.second+1]--;
        if(p.second % 2 == 0){
            z[p.first+1]--;
            z[p.first+p.second]++;
        }
    }

    ll ans = INT64_MAX;
    for(auto it=z.begin();it!=z.end();++it){
        if(it->second % 2 != 0){
            ans = min(ans, it->first);
        }
    }

