ll solve(int N, vll& x, vll& y) {
    map<ll,int> st;
    priority_queue<llll, vector<llll>, greater<llll>> pq;
    rep(i,N){
        if (y[i] < 0) {
            pq.emplace(y[i], x[i]);
        }
    }
    while (!pq.empty()) {
        if (pq.top().first == 0) {
            llll fst = pq.top(); pq.pop();
            ll xi = fst.second;
            st[xi] = !st[xi];
            continue;
        }
        llll fst = pq.top(); pq.pop();
        ll yi = fst.first, xi = fst.second;
        map<ll,int> xc; xc[xi]++;
        while (!pq.empty() && pq.top().first == fst.first) {
            ll xj = pq.top().second;
            xc[xj] = !xc[xj];
            pq.pop();
        }
        vll xs;
        for(auto &p: xc) {
            if (p.second) xs.pb(p.first);
        }
        if (xs.size() % 2) return LLONG_MIN;

        for (int i=0; i<xs.size(); i+=2) {
            ll d = xs[i+1] - xs[i];
            ll yd = yi + d;
            if (yd > 0) {
                st[xs[i]] = !st[xs[i]];
                st[xs[i]+yd] = !st[xs[i]+yd];
            } else if (yd == 0) {
                st[xs[i]] = !st[xs[i]];
            } else {
                pq.emplace(yd, xs[i]);
            }
        }
    }
    rep(i,N){
        if (y[i] == 0) {
            st[x[i]] = !st[x[i]];
        } else if (y[i] > 0) {
            st[x[i]] = !st[x[i]];
            st[x[i]+y[i]] = !st[x[i]+y[i]];
        } else {
            ;
        }
    }
    for (auto &p: st) {
        if (p.second) return p.first;
    }
    return 0;
}

int main() {
    int N; scanf("%d", &N);
    vll x(N), y(N);
    rep(i,N){
        scanf("%lld%lld", &x[i], &y[i]);
    }
    printf("%lld\n", solve(N,x,y));
    return 0;
}

