bool ParityXorNotExceeding(LL x, LL y, LL max_val) {
  if (x > max_val) { return 0; }

  int low_bit = 0;
  while (!((y >> low_bit) & 1)) { ++low_bit; }

  max_val -= x;
  for (int bit = 60; bit > low_bit; --bit) {
    bool max_bit = ((max_val >> bit) & 1);
    bool y_bit = ((y >> bit) & 1);
    if (max_bit && !y_bit) {
      return false;
    }
  }

  return !((max_val >> low_bit) & 1);
}

