int solve(LL y, LL t) {
    if (t < 0) return 0;
    if (t >= y) return y==0;
    LL res = 1;
    int bity = 0;
    for (int st = 0; st <= 60; st++) {
        LL mm = 1ll<<st;
        if (t & mm) {
            if ((y & mm) == 0) res = 1ll<<bity;
            else res += 1ll<<bity;
        }
        if (y & mm) bity++;
    }
    return res % 2; // Maybe there is a faster way?
}

