ll mod_pow(ll a, ll n) {
	ll res = 1;
	while (n) {
		if (n & 1)res = res * a%mod;
		a = a * a%mod; n >>= 1;
	}
	return res;
}

int calc(ll y, ll x, int sta = 59) {
	if (x <= 0)return 0;
	if (y < 0) {
		ll memo = 1;
		while (memo < -y)memo *= 2;
		ll d = x / memo;
		int res = 0;
		if (d % 2)res ^= calc(y + memo, memo);
		ll r = x % memo;
		res ^= calc(y + memo, r);
		return res;
	}
	if (y == 0)return 1;
	ll memo = 1;
	int chk;
	for (int i = sta; i >= 0; i--) {
		ll k = (ll)1 << i;
		if (k <= y) {
			memo = k; chk = i - 1; break;
		}
	}
	while (memo * 2 <= y)memo *= 2;
	if (memo <= x) {
		int res = calc(y - memo, x - memo,chk);
		if (memo == y)res ^= 1;
		return res;
	}
	else {
		return calc(y - memo, x,chk);
	}
}
void solve() {
