	rep(i, n) {
		cin >> x[i] >> y[i];
	}
	ll le = -(1e+17) - 1; ll ri = -le;
	while (ri - le > 1) {
		ll mid = (le + ri) / 2;
		int res = 0;
		rep(i, n) {
			ll dy = y[i];
			ll dx = mid - x[i] + 1;
			res ^= calc(dy, dx);
		}
		if (res)ri = mid;
		else le = mid;
	}
	cout << ri << endl;
}


