ll solve(int N, vll& x, vll& y) {
    map<ll,int> st;
    rep(i,N){
        if (y[i] == 0) {
            st[x[i]] = !st[x[i]];
        } else {
            st[x[i]] = !st[x[i]];
            st[x[i]+y[i]] = !st[x[i]+y[i]];
        }
    }
    for(auto &p: st) {
        if (p.second) return p.first;
    }
    return 0;
}

