#include<iostream>
using namespace std;
typedef long long ll;
int n;
ll x[100001],y[100001];
ll px[200001],py[200001];
bool pz[200001];
ll tae(ll x,ll y,int id){//y<2^59
	if(x==px[id] && y==py[id]) return pz[id];
	bool eq=1,sm=0;
	for(int i=58; i>=0 ;i--){
		bool neq=0,nsm=0;
		if((x&(1LL<<i))!=0){
			if((y&(1LL<<i))==0) nsm^=eq;
			else neq^=eq,nsm^=eq;
		}
		else neq^=eq;
		if((y&(1LL<<i))==0) nsm^=sm;
		eq=neq,sm=nsm;
	}
	px[id]=x,py[id]=y,pz[id]=(eq^sm);
	return eq^sm;
}
int qry(ll l,ll r){
	int sum=0;
	for(int i=1; i<=n ;i++){
		ll ty=y[i]+(1LL<<58);
		ll xl=x[i],xr=x[i]+ty;
		xl=max(xl,l);
		xr=min(xr,r);
		if(xl>xr) continue;
		sum^=tae(xr-x[i],ty,i*2-1)%2;
		if(xl!=x[i]) sum^=tae(xl-x[i]-1,ty,i*2);
	}
	return sum;
}
int main(){
	ios::sync_with_stdio(false);
	cin >> n;
	for(int i=1; i<=n ;i++){
		cin >> x[i] >> y[i];
	}
	ll l=-1e17,r=1e17;
	while(l!=r){
		ll mid=l+(r-l)/2;
		int sum=qry(l,mid);
		if(sum%2==0) l=mid+1;
		else r=mid;
	}
	cout << l << endl;
}

