using namespace std;



struct MyCompare {
	bool operator() (string s1, string s2) {
		long long int x1, x2, y1, y2;
		x1 = stoll(string(s1.begin(), s1.begin() + s1.find(' ')));
		y1 = stoll(string(s1.begin() + s1.find(' ') + 1, s1.end()));
		x2 = stoll(string(s2.begin(), s2.begin() + s2.find(' ')));
		y2 = stoll(string(s2.begin() + s2.find(' ') + 1, s2.end()));
		if (y1 == y2) 
			return x1 < x2;
		else {
			if (y1 == 0) return 1;
			if (y2 == 0) return 0;
			if (y1 * y2 < 0)
				return y1 < y2;
			else 
				return abs(y1) < abs(y2);
		}
	}
} myCompare;

int main () {
	int n;
	cin >> n;
	unordered_map<string, int> um;
	priority_queue<string, vector<string>, MyCompare> pq (myCompare);

	for (int i = 0; i < n; i++) {
		long long int x, y;
		cin >> x >> y;
		string s = to_string(x) + " " + to_string(y);
		pq.push(s);
		um[s] = 1;
	}

	while (pq.size() > 0) {
		string s = pq.top();
		pq.pop();
		if (um[s] == 0)
			continue;
		long long int x, y;
		x = stoll(string(s.begin(), s.begin() + s.find(' ')));
		y = stoll(string(s.begin() + s.find(' ') + 1, s.end()));

		if (y > 0) {
			string s1 = to_string(x) + " " + to_string(y - 1);
			string s2 = to_string(x + 1) + " " + to_string(y - 1);
			um[s1] = 1 -um[s1];
			um[s2] = 1 - um[s2];
			if (um[s1] == 1)
				pq.push(s1);
			if (um[s2] == 1)
				pq.push(s2);
		}
		else if (y < 0) {
			string s1 = to_string(x - 1) + " " + to_string(y);
			string s2 = to_string(x - 1) + " " + to_string(y - 1);
			um[s1] = 1 - um[s1];
			um[s2] = 1 - um[s2];
			if (um[s1] == 1)
				pq.push(s1);
			if (um[s2] == 1)
				pq.push(s2);
		}
		else {
			cout << x;
			return 0;
		}
	}

}

