    auto apply = [&](int x, int y) {
        if (y == 0) {
            line[x] ^= 1;
        } else if (y == 1) {
            line[x] ^= 1;
            line[x + 1] ^= 1;
        } else if (y % 2 == 0) {
            line[x] ^= 1;
            line[x + y] ^= 1;
        } else {
            line[x] ^= 1;
            line[x + 1] ^= 1;
            line[x + y - 1] ^= 1;
            line[x + y] ^= 1;
        }
    };

    for (int i = 0; i < n; ++i){
