    while (!neg.empty()){
        assert(neg.size() > 1);
        pii a = *neg.begin();
        neg.erase(a);
        pii b = *neg.begin();
        neg.erase(b);
        assert(a.first == b.first);

        int64 x = a.second, y = a.first;
        int64 d = b.second - a.second;

        int64 nx = x, ny = y + d;

