const ll MAX=1e17;
bool count(ll y, ll m){
	if(y==0) return 1;
	else if(y>0){
		bool dp[2][59]={};
		for(int i=0; i<59; i++){
			if((1ll<<i)<=m && ((1ll<<i)|y)==y){
				if(m<(1ll<<(i+1))) dp[0][i]=1;
				else dp[1][i]=1;
			}
		}
		for(int i=57; i>=0; i--){
			if(y&(1ll<<i)){
				if(!dp[0][i+1]) continue;
				if(m&(1ll<<i)){
					dp[1][i]^=1;
					dp[0][i]^=1;
				}else{
					dp[0][i]^=1;
				}
			}else{
				dp[1][i]^=dp[1][i+1];
				if(m&(1ll<<i)){
					dp[1][i]^=dp[0][i+1];
				}else{
					dp[0][i]^=dp[0][i+1];
				}
			}
		}
		return (dp[0][0]^dp[1][0]^1);
	}else{
		y=-y;
		y--;
		if(y==0){
			return (m+1)&1ll;
		}
		m+=y;
		bool dp[2][59]={};
		for(int i=0; i<59; i++){
			if((1ll<<i)<=m && y<(1ll<<(i+1))){
				if(m<(1ll<<(i+1))) dp[0][i]=1;
				else dp[1][i]=1;
			}
		}
		for(int i=57; i>=0; i--){
			if(!(y&(1ll<<i))){
				if(!dp[0][i+1]) continue;
				if(m&(1ll<<i)){
					dp[1][i]^=1;
					dp[0][i]^=1;
				}else{
					dp[0][i]^=1;
				}
			}else{
				dp[1][i]^=dp[1][i+1];
				if(m&(1ll<<i)){
					dp[0][i]^=dp[0][i+1];
				}
			}
		}
		return (dp[0][0]^dp[1][0]);
	}
}
