inline long long mod(long long a, long long m) {
    return (a % m + m) % m;
}

//  Euclid 
// ap + bq = gcd(a, b)  (p, q) d = gcd(a, b) 
long long extGcd(long long a, long long b, long long &p, long long &q) {  
    if (b == 0) { p = 1; q = 0; return a; }  
    long long d = extGcd(b, a%b, q, p);  
    q -= a/b * p;  
    return d;  
}

// 
//  (r, m)  x  r (mod. m)
//  (0, -1) 
pair<long long, long long> ChineseRem(long long b1, long long m1, long long b2, long long m2) {
  long long p, q;
  long long d = extGcd(m1, m2, p, q); // p is inv of m1/d (mod. m2/d)
  if ((b2 - b1) % d != 0) return make_pair(0, -1);
  long long m = m1 * (m2/d); // lcm of (m1, m2)
  long long tmp = (b2 - b1) / d * p % (m2/d);
  long long r = mod(b1 + m1 * tmp, m);
  return make_pair(r, m);
}


ll powmod(ll x, ll n, ll mo){
    n%=(mo-1);
    x%=mo;
    if(n<0)n+=mo-1;
    ll ret=1;
    while(n){
        if(n%2)ret=ret*x %mo;
        x=x*x%mo;
        n/=2;
    }
    return ret;
}

ll func(ll x,ll y, ll mo){
    ll ret=powmod(2,y-1,mo);
    ret=ret*((2*x+y)%mo)%mo;
    if(y%2)return mo -ret;
    else return ret;
}
int main(){
