        ret1+=func(x,y,mod1);
        ret2+=func(x,y,mod2);
    }
    ret1%=mod1;
    ret2%=mod2;
    ll ans=ChineseRem(ret1,mod1,ret2,mod2).first;
    if(ans>1e17)ans-=mod1*mod2;
    cout<<ans<<endl;

    return 0;
}

