  for (int bit = 0; bit < 60; ++bit) {
    if ((y >> bit) & 1) { continue; }
    if ((1LL << bit) <= y) { continue; }

    LL overflow_bound = (1LL << bit) - (x & ((1LL << bit) - 1));
    LL my_y = 0;
    //debug(bit, overflow_bound, low_bit);
    bool fail = false;
    for (int b = bit - 1; b > low_bit; --b) {
      bool ov_bit = ((overflow_bound >> b) & 1);
      bool y_bit = ((y >> b) & 1);
      if (ov_bit && !y_bit) { fail = true; break; }
      my_y |= ((LL)ov_bit << b);
    }

    int num_over = (my_y >= overflow_bound);
    num_over += ((my_y ^ (1LL << low_bit)) >= overflow_bound);
    if (!fail && num_over == 1) {
      answer += 1LL << bit;
    }
  }
  return answer;
}

LL BruteXorPositions(LL x, LL y) {
  LL ans = 0;
  for (LL i = 0; i <= y; ++i) {
    if ((y & i) == i) {
      ans ^= (x + i);
    }
  }
  return ans;
}

int32_t main() {
  ios_base::sync_with_stdio(0);
  cin.tie(0);
  cout << fixed << setprecision(11);
  cerr << fixed << setprecision(6);

  int N;
  cin >> N;

  LL xor_up = 0;
  set<pair<LL, LL>, greater<pair<LL, LL>>> belows;

  for (int i = 0; i < N; ++i) {
    LL x, y;
    cin >> x >> y;
    if (y >= 0) {
      x += ShiftAll;
      xor_up ^= GetXorPositions(x, y);
    } else {
      belows.emplace(-y, x);
    }
  }

  while (!belows.empty()) {
    assert(SZ(belows) >= 2);
    auto p1 = *belows.begin();
    belows.erase(belows.begin());
    auto p2 = *belows.begin();
    belows.erase(belows.begin());
    assert(p1.st == p2.st);
    swap(p1, p2);
    assert(p1.nd < p2.nd);

    const int dist = p2.nd - p1.nd;
    assert(dist <= p1.st);
    PII new_p{p1.st - dist, p1.nd};

    if (new_p.st == 0) {
      xor_up ^= new_p.nd + ShiftAll;
    } else if (belows.count(new_p)) {
      belows.erase(new_p);
    } else {
      belows.insert(new_p);
    }
  }

  cout << xor_up - ShiftAll << "\n";
}

