int calc(ll y, ll x) {
	if (x <= 0)return 0;
	if (y < 0) {
		ll memo = 1;
		while (memo < -y)memo *= 2;
		ll d = x / memo;
		int res = 0;
		if (d % 2)res ^= calc(y + memo, memo);
		ll r = x % memo;
		res ^= calc(y + memo, r);
		return res;
	}
	if (y == 0)return 1;
	ll memo = 1;
	while (memo * 2 <= y)memo *= 2;
	if (memo <= x) {
		int res = calc(y - memo, x - memo);
		if (memo == y)res ^= 1;
		return res;
	}
	else {
		return calc(y - memo, x);
	}
}
