ll calcdesc(ll dy) { int bits=0; while(dy>0) bits+=dy&1,dy>>=1; return 1LL<<bits;  }

ll calccnt(P a,ll mx) {
	ll ret=0;
	while(true) {
		if(a.y==qy) { if(a.x<=mx) ++ret; break; }
		int step=0; while(a.y-(2LL<<step)>=qy) ++step;
		//printf("(%lld,%lld) -> step=%d\n",a.x,a.y,step);
		P b(a.x,a.y-(1LL<<step)),c(a.x+(1LL<<step),a.y-(1LL<<step));
		//printf("(%lld,%lld) / (%lld,%lld)\n",b.x,b.y,c.x,c.y);
		if(b.x+b.y-qy<=mx) { ret+=calcdesc(b.y-qy); a=c; } else { a=b; }
	}
	return ret;
}


ll calcsum(ll mx,ll mod) {
	ll ret=0; REP(i,n) ret=(ret+calccnt(p[i],mx))%mod; return ret;
}

ll solve() {
	ll ly=LLONG_MAX; REP(i,n) ly=min(ly,p[i].y);
	ll lx=LLONG_MAX,hx=LLONG_MIN; REP(i,n) lx=min(lx,p[i].x),hx=max(hx,p[i].x+p[i].y-ly);
	ll dx=(hx-lx)/2+1; while((dx&-dx)!=dx) dx+=dx&-dx; qy=-dx; assert(qy<=ly);
