		printf("%3lld: ",qy); for(ll x=lx;x<=hx;++x) printf(" %lld",calcsum(x,1LL<<20)); puts("");
	}*/

	assert(calcsum(lx+dx-1,2)%2==1);
	ll lo=lx-1,hi=lx+dx-1; while(lo+1<hi) { ll mi=lo+(hi-lo)/2; if(calcsum(mi,2)%2==1) hi=mi; else lo=mi; }
	return hi;
}


