signed main(){
	int N, i;
	scanf("%lld", &N);
	vector<int> x(N), y(N);
	int M = 0;
	for(i = 0; i < N; i++){
		scanf("%lld%lld", &x[i], &y[i]);
		M = max(M, max(abs(x[i]), abs(y[i])));
	}
	int T = 1;
	while(T < 3 * M){
		T = 2 * T + 1;
	}
//	printf("(M, T) = (%lld, %lld)\n", M, T);
	int l = -(M + 1), h, r = 2 * M;
	while(r - l > 1){
		h = (l + r) / 2;
//		printf("h = %lld\n", h);
		int is_black = 0;
		for(i = 0; i < N; i++){
			is_black ^= comb_mod2(y[i] + T, h - x[i]);
//			printf("is_black[%lld] = %lld\n", i, is_black);
		}
//		printf("is_black = %lld\n", is_black);
		if(is_black == 1){
			r = h;
		}
		else{
			l = h;
		}
	}
	printf("%lld\n", r);
	return 0;
}

