    const LL INF = 1ll<<59;
    for (int i = 0; i < N; i++) {
        cin >> pt[i].first >> pt[i].second;
        pt[i].first += INF;
        if (pt[i].second < 0) {
            pt[i].first += pt[i].second;
            pt[i].second *= -1;
        }
    }
    vector<int> op;
    for (int step = 0; step < 64; step++) {
        vector<pll> new_pt;
        LL sum = 0;
        for (auto pp : pt) {
            LL x = pp.first, y = pp.second;
            if (y == 0) sum = (sum + x) % 2;
            if (y == 1) sum = (sum + 1) % 2;
        }
        if (sum % 2 == 0) op.push_back(0);
        else op.push_back(1);
        for (auto pp : pt) {
            LL x = pp.first, y = pp.second;
            if (sum % 2 == 0) {
                if (x % 2 == 0) new_pt.push_back({x/2, y/2});
                if (x % 2 == 1 and y % 2 == 1) new_pt.push_back({(x+1)/2, y/2});
            } else {
                if (x % 2 == 0 and y % 2 == 1) new_pt.push_back({x/2, y/2});
                if (x % 2 == 1) new_pt.push_back({(x-1)/2, y/2});
            }
        }
        pt = new_pt;
    }
    LL res = 0;
    reverse(op.begin(), op.end());
    for (int x : op) res = 2*res + x;
    cout << res - INF<< "\n";

}

