typedef long long ll;
int n;
ll x[100001],y[100001];
ll px[200001],py[200001];
bool pz[200001];
ll tae(ll x,ll y,int id){//y<2^59
	if(x==px[id] && y==py[id]) return pz[id];
	bool eq=1,sm=0;
	for(int i=58; i>=0 ;i--){
		if((x&(1LL<<i))){
			if((y&(1LL<<i))) sm=eq;
			else{sm^=eq;eq=0;}
		}
		else{
			if((y&(1LL<<i))) sm=0;
		}
	}
	px[id]=x,py[id]=y,pz[id]=(eq^sm);
	return eq^sm;
}
int qry(ll l,ll r){
	int sum=0;
	for(int i=1; i<=n ;i++){
		ll ty=y[i]+(1LL<<58);
		ll xl=max(0LL,l-x[i]);
		ll xr=min(ty,r-x[i]);
		if(xl>xr) continue;
		sum^=tae(xr,ty,i*2-1)%2;
		if(xl!=x[i]) sum^=tae(xl-1,ty,i*2);
	}
	return sum;
}
