namespace Problem {
using namespace std;

class Solver {
 public:
  int n;
  vector<LL> x, y;
  LL N = (1ll << 60) - 2;
  Solver(LL n) : n(n), x(n), y(n){};
  bool isOdd(LL a, LL b) {
    if (a < b) return false;
    if (b < 0) return false;
    //if (a < b) swap(a, b);
    return (a - b) == (a ^ b);
  }
  int coeffMod2(LL ord) {
    int ret = 0;
    for (int i = 0; i < n; ++i) {
      ret += isOdd(y[i] + N, ord - x[i]);
    }
    DBG(ord, ret % 2)
    return ret % 2;
  }
  void solve() {
    for (int i = 0; i < n; ++i) {
      cin >> x[i] >> y[i];
    }
    //*(1+x)^(2^60-2)
    //t^0
    if (coeffMod2((1ll << 57))) {
      LL lb = -(1ll << 57), ub = (1ll << 57);
      while (ub - lb > 2) {
        LL mid = (ub + lb) / 2;
        if (coeffMod2(mid)) {
          ub = mid;
        } else {
          lb = mid;
        }
      }
      cout << ub << endl;
    } else {
      assert(coeffMod2((1ll << 57) + 1));
      LL lb = -(1ll << 57) + 1, ub = (1ll << 57) + 1;
      while (ub - lb > 2) {
        LL mid = (ub + lb) / 2;
        if (coeffMod2(mid)) {
          ub = mid;
        } else {
          lb = mid;
        }
      }
      cout << ub << endl;
    }
  }
};  // namespace Problem
