void f(vector<pair<ll, ll>> &a, function<pair<ll, ll>(pair<ll, ll>)> encode,
       function<pair<ll, ll>(pair<ll, ll>)> decode) {
  map<ll, map<ll, bool>> m;
  for (auto e : a) {
    auto p = encode(e);
    m[p.first][p.second] ^= true;
  }
  a.clear();
  while (!m.empty()) {
    ll k = begin(m)->first;
    auto h = begin(m)->second;
    m.erase(begin(m));
    for (auto &e : h) {
      for (ll w = 1; e.second && e.first + w <= 1e17; w *= 2) {
        if (h.count(e.first + w) && h[e.first + w]) {
          e.second = false;
          h[e.first + w] = false;
          m[k + w][e.first] ^= true;
        }
      }
      if (e.second) {
        a.push_back(decode({k, e.first}));
      }
    }
  }
}

