#include <bits/stdc++.h>

using namespace std;

const long long base = 1ll << 57;
const int LOG = 60;

int main() {
#ifdef wxh010910
  freopen("input.txt", "r", stdin);
#endif
  int n;
  cin >> n;
  vector<long long> x(n), y(n);
  for (int i = 0; i < n; ++i) {
    cin >> x[i] >> y[i];
    x[i] += base;
    y[i] += base;
  }
  vector<bool> equal(3), less(3), new_equal(3), new_less(3);
  auto solve = [&](long long p, int remainder) {
    bool ans = false;
    for (int i = 0; i < n; ++i) {
      if (p > y[i]) {
        long long high = p - y[i];
        for (int j = 0; j < 3; ++j) {
          equal[j] = less[j] = false;
        }
        equal[0] = 1;
        for (int j = LOG - 1; ~j; --j) {
          for (int k = 0; k < 3; ++k) {
            new_equal[k] = new_less[k] = false;
          }
          {
            int l = 0, r = min(x[i] >> j & 1, high >> j & 1);
            for (int k = 0; k < 3; ++k) {
              if (equal[k]) {
                for (int w = l; w <= r; ++w) {
                  int kk = (k + w * ((j & 1) + 1)) % 3;
                  if (w == (high >> j & 1)) {
                    new_equal[kk] = !new_equal[kk];
                  } else {
                    new_less[kk] = !new_less[kk];
                  }
                }
              }
            }
          }
          {
            int l = 0, r = x[i] >> j & 1;
            for (int k = 0; k < 3; ++k) {
              if (less[k]) {
                for (int w = l; w <= r; ++w) {
                  int kk = (k + w * ((j & 1) + 1)) % 3;
                  new_less[kk] = !new_less[kk];
                }
              }
            }
          }
          swap(equal, new_equal);
          swap(less, new_less);
        }
        ans ^= less[(remainder - y[i] % 3 + 3) % 3];
      }
    }
    return ans;
  };
  auto check = [&](long long p) {
    bool ans = false;
    for (int i = 0; i < n; ++i) {
      if (p - y[i] >= 0 && p - y[i] <= x[i] && (x[i] & (p - y[i])) == p - y[i]) {
        ans = !ans;
      }
    }
    return ans;
  };
  int remainder = 0;
  while (!solve(1ll << LOG, remainder)) {
    ++remainder;
  }
  long long p = 0;
  for (int i = LOG - 1; ~i; --i) {
    if (solve(p, remainder) == solve(p + (1ll << i), remainder)) {
      p += 1ll << i;
    }
  }
  long long l = p;
  for (int i = LOG - 1; ~i; --i) {
    if (check(l - (1ll << i))) {
      l -= 1ll << i;
    }
  }
  long long r = p;
  for (int i = LOG - 1; ~i; --i) {
    if (check(r + (1ll << i))) {
      r += 1ll << i;
    }
  }
  cout << r - l - base /* << " " << l - base */ << "\n";
  return 0;
}

