pin solve(V<pin>& ab, int ex) {
	if (ex == 0) { return mkp(1, 0); }
	V<pin> ab0, ab1;
	for (auto x : ab) {
		int a1 = x.first;
		int b1 = x.second;
		if (b1 % 2 == 0) {
			ab0.push_back(mkp(a1 / 2,b1 / 2));
			if (a1 % 2 == 1) { ab1.push_back(mkp(a1 / 2, b1 / 2)); }
		}
		else {
			ab1.push_back(mkp(a1 / 2, b1 / 2));
			if (a1 % 2 == 1) { ab0.push_back(mkp(a1 / 2, (b1 + 1) / 2)); }
		}
	}
	vel c0(2), c1(2);
	for (auto x : ab0) {
		int qa = x.first % 3; qa = 3 - qa;
		int qb = x.second % 3;
		int qc = (qa + qb) % 3;
		if (qc == 0) { c0[0] ^= 1; }
		if (qc == 1) { c0[1] ^= 1; }
		if (qc == 2) { c0[0] ^= 1; c0[1] ^= 1; }
	}
	for (auto x : ab1) {
		int qa = x.first % 3; qa = 3 - qa;
		int qb = x.second % 3;
		int qc = (qa + qb) % 3;
		if (qc == 0) { c1[0] ^= 1; }
		if (qc == 1) { c1[1] ^= 1; }
		if (qc == 2) { c1[0] ^= 1; c1[1] ^= 1; }
	}
	vel ze(2, 0);
	if (c0 == ze) {
		pin ans=solve(ab1, ex-1);
		ans.first *= 2; ans.second *= 2;
		ans.second++;
		return ans;
	}
	if (c1 == ze) {
		pin ans = solve(ab0, ex - 1);
		ans.first *= 2; ans.second *= 2;
		return ans;
	}
	rep(i,ab.size()) {ab[i].first++; }
	pin ans = solve(ab, ex);
	ans.first--;
	return ans;
}
