int calc(ll y, ll x) {
	if (x <= 0)return 0;
	if (y < 0) {
		ll memo; int chk;
		rep(i, 60) {
			ll k = (ll)1 << i;
			if (y <= k) {
				memo = k, chk = i; break;
			}
		}
		ll d = x / memo;
		int res = 0;
		res ^= (d % 2)*calc(y + memo, memo);
		ll r = x % memo;
		res ^= calc(y + memo, r);
		return res;
	}
	if (y == 0)return 1;
	ll memo;
	rep(i, 60) {
		ll k = (ll)1 << i;
		if (k <= y) {
			memo = k;
		}
	}
	if (memo <= x) {
		int res = calc(y - memo, x - memo);
		if (memo == y)res ^= 1;
