void chemthan() {
    map<pair<long long, long long>, int> hs1, hs2;
    int n; cin >> n;
    FOR(i, 0, n) {
        long long x, y; cin >> x >> y;
        hs1[mp(x, y)] = hs2[mp(y, x)] = 1;
    }
    while (1 < sz(hs1)) {
        if (0 < hs2.rbegin()->fi.fi) {
            long long y = hs2.rbegin()->fi.fi;
            long long x = hs2.rbegin()->fi.se;
            hs1.erase(mp(x, y));
            hs2.erase(mp(y, x));
            for (auto e : {mp(x, y - 1), mp(x + 1, y - 1)}) {
                if (hs1.count(e)) {
                    hs1.erase(e);
                }
                else {
                    hs1[e] = 1;
                }
                swap(e.fi, e.se);
                if (hs2.count(e)) {
                    hs2.erase(e);
                }
                else {
                    hs2[e] = 1;
                }
            }
        }
        else if (hs2.begin()->fi.fi < 0) {
            long long y = hs2.begin()->fi.fi;
            long long x = hs2.begin()->fi.se;
            hs1.erase(mp(x, y));
            hs2.erase(mp(y, x));
            for (auto e : {mp(x, y + 1), mp(x + 1, y)}) {
                if (hs1.count(e)) {
                    hs1.erase(e);
                }
                else {
                    hs1[e] = 1;
                }
                swap(e.fi, e.se);
                if (hs2.count(e)) {
                    hs2.erase(e);
                }
                else {
                    hs2[e] = 1;
                }
            }
        }
        else if (hs1.begin()->fi < hs1.rbegin()->fi) {
            long long x = hs1.rbegin()->fi.fi;
            long long y = hs1.rbegin()->fi.se;
            hs1.erase(mp(x, y));
            hs2.erase(mp(y, x));
            for (auto e : {mp(x - 1, y), mp(x - 1, y - 1)}) {
                if (hs1.count(e)) {
                    hs1.erase(e);
                }
                else {
                    hs1[e] = 1;
                }
                swap(e.fi, e.se);
                if (hs2.count(e)) {
                    hs2.erase(e);
                }
                else {
                    hs2[e] = 1;
                }
            }
        }
    }
    cout << hs1.begin()->fi.fi << "\n";
}

