            if(AA.count({ker[i].F-two,ker[i].S+two})){
                AA.erase({ker[i].F-two,ker[i].S+two});
                AA.erase(ker[i]);
                PLL nxt={ker[i].F-two,ker[i].S};
                if(!AA.count(nxt)){
                    AA.insert(nxt);
                    ker[rr++]=nxt;
                }
                else{
                    AA.erase(nxt);
                }
                break;
            }
        }
    }
    printf("%lld\n",(long long)(AA.begin()->F));
    return 0;
}

